        [TestMethod]
        public void TestErrorMessageTrue()
        {
            OpenALPRParser parser = new OpenALPRParser();
            bool isError = parser.IsError("{\"error_code\":\"true\"}");
            Assert.IsTrue(isError);
        }

        [TestMethod]
        public void TestErrorMessageFalse()
        {
            OpenALPRParser parser = new OpenALPRParser();
            bool isError = parser.IsError("{}");
            Assert.IsFalse(isError);
